import React from 'react';
import {useState} from 'react';
import { Link } from 'react-router-dom';

function Header(props) {
  const [isMenu, setIsMenu] = useState(false);

  function burgerClick () {
    setIsMenu(!isMenu);
  }
  return (
    <header className={`header ${isMenu && 'header_menu-show'}`}>
        <div className="header__logo"></div>
        <div className="header__nav">

          <div className="header__nav_right">
            <p className={'header__status header__email'}>{props.emailUser}</p> 
            <Link to={props.nav} className={'header__link header__status'} onClick={props.onLogout}>{props.navStatus}</Link>}
          </div>
          <div className="header__burger-wrapper" onClick={burgerClick}>
          <div className={`header__burger ${isMenu && 'header__burger_active'}`}></div>
        </div>
        </div>
    </header>
  );
}
